create table menu
(
    id          int auto_increment comment '菜单主键'
        primary key,
    version     int          null comment '版本号',
    create_time datetime     null comment '创建时间',
    update_time datetime     null comment '更新时间',
    is_deleted  int          null comment '逻辑删除（0：未删除；1：删除）',
    title       varchar(255) null comment '菜单名称',
    path        varchar(255) null comment '菜单路由',
    component   varchar(255) null comment '菜单组件',
    parent_id   int          null comment '父级菜单主键',
    order_num   int          null comment '菜单顺序',
    level       int          null comment '菜单等级（1：一级菜单；2：二级菜单；3：操作按钮）',
    tag         varchar(255) null comment '权限标记',
    status      int          null comment '开启状态（0：开启；1：关闭）'
);

